mapper.select:
select pbf.id id,pbf.type type,pbf.user_id userId,su.username userNo,su.`name` userName,pbf.money money,
pbf.frozen_type frozenType,pbf.`status` `status`,pbf.remark remark,pbf.jd_time jdTime
from pay_balance_frozen pbf
inner join sys_user su on su.id=pbf.user_id


        and su.id=#{merchantId} and su.type=2



        and su.id=#{proxyId} and su.type=1



        and pbf.frozen_type=#{frozenType}





    order by ${orderby}


    order by pbf.CREATE_TIME desc

limit #{start},#{end}

mapper.update:
update pay_balance_frozen
set status=#{status}
where id=#{id}

update pay_client_channel
set status=#{status},jd_time=SYSDATE()
where id=#{id}
