mapper.select:
SELECT id,card_no cardNo,card_name cardName,bank_name bankName,bank_subname bankSubname,card_id cardId, card_mobile cardMobile,bank_code bankCode,user_id userId,user_no userNo,user_name userName,user_type userType ,province,city,bank_ifsc bankIfsc,bank_nation bankNation,btype from pay_bank_card
where del=0
and card_no like "%"#{cardNo}"%"
and card_name like "%"#{cardName}"%"
and bank_name like "%"#{bankName}"%"
and bank_code like "%"#{bankCode}"%"
and user_id=#{userId}
and user_no like "%"#{userNo}"%"
and btype=#{btype}
order by ${orderby}
order by UPDATE_TIME desc
limit #{start},#{end}

select id,card_no cardNo,card_name cardName,bank_name bankName,bank_subname bankSubname,card_id cardId, card_mobile cardMobile,bank_code bankCode,user_id userId,user_no userNo,user_name userName,user_type userType,province,city,bank_ifsc bankIfsc,bank_nation bankNation,btype from pay_bank_card where del=0
and id=#{id}
and bank_code=#{bankCode}
limit 1

mapper.update:
update pay_bank_card
set card_no=#{cardNo},
card_name=#{cardName},
bank_name=#{bankName},
bank_subname=#{bankSubname},
card_id=#{cardId},
card_mobile=#{cardMobile},
bank_code=#{bankCode},
province=#{province}
,city=#{city}
,bank_ifsc=#{bankIfsc}
,bank_nation=#{bankNation}
,btype=#{btype}
where id=#{id}
