mapper.select:
select client.id,client.username username,client.`name` `name`,IFNULL(pcd.deposit,0) deposit,IFNULL(pcd.money,0) money, case when pcd.paramcode is not null and pcd.querydealcode is not null then 1 else 0 end api,IFNULL(pcd.reqtype,0) reqtype, pcd.vs vs from sys_user client left join pay_client_deposit pcd on client.id=pcd.client_id where type=3
and username like "%"#{username}"%"
order by ${orderby}
limit #{start},#{end}

select client.id id,client.username username,client.`name` `name`,pcd.deposit deposit,pcd.money money,
pcd.paramcode paramcode,pcd.querydealcode querydealcode,pcd.reqtype reqtype,pcd.vs vs
from sys_user client
left join pay_client_deposit pcd on client.id=pcd.client_id
where client.id=#{id}

    and pcd.client_id=#{clientId}
