SELECT id,`name`,keyname,filepath,status,order_status orderStatus,pay_status payStatus,req_type
reqType,version,test_status testStatus,notifystr,cd_status cdStatus
from pay_client_model



        and `name` like "%"#{name}"%"


        and keyname like "%"#{keyname}"%"



        and status=#{status}



        and order_status=#{orderStatus}



        and pay_status=#{payStatus}



        and test_status=#{testStatus}



        and req_type=#{reqType}



limit #{start},#{end}

