mapper.select:
SELECT id,CREATE_TIME createTime,type,user_name userName,sn,descript,sn_type snType
from pay_log



        and type=#{type}



        and sn_type=#{snType}



        and sn like "%"#{sn}"%"





    order by ${orderby}


    order by CREATE_TIME desc


limit #{start},#{end}

SELECT count(1) count
from pay_log



        and type=#{type}



        and sn_type=#{snType}



        and sn like "%"#{sn}"%"

SELECT user_id userId ,username,time,opt_type optType, opt_content optContent,link_content linkContent,ip
from pay_sensitive_log



        and opt_type=#{optType}





    order by ${orderby}


    order by time desc


limit #{start},#{end}

SELECT count(1) count
from pay_sensitive_log



        and opt_type=#{optType}
