mapper.select:
select paytime paytime,profit profit,succount succount,total total,cashprofit cashprofit,profit+cashprofit
totalprofit,case when total=0 then '0%' else concat(FORMAT(succount*100/total,2),'%') end ratio
from (
select paytime,sum(profit) profit,sum(succount) succount,sum(total) total,sum(cashprofit) cashprofit
from (
select paytime,sum(profit) profit,sum(succount) succount,sum(total) total,0 cashprofit
from (
select case when order_status=1 then 1 else 0 end succount,case when order_status=1 then IFNULL(profit,0) else 0
end profit,DATE_FORMAT(create_time,${timeFormat}) paytime,1 total
from pay_order
where create_time is not null and xnorder=0

    and create_time   >=   #{startTime}


    and create_time   <   #{endTime}

) a
GROUP BY paytime
union all
select paytime,0 profit,0 succount,0 total,sum(cashprofit) cashprofit
from (
select DATE_FORMAT(UPDATE_TIME,${timeFormat}) paytime,IFNULL(commission,0)-IFNULL(proxy_money,0) cashprofit
from pay_cash
where pay_status=5 and user_type=0

    and pay_time   >=   #{startTime}


    and pay_time   <   #{endTime}

) a
GROUP BY paytime
) b
GROUP BY paytime
) c


    order by ${orderby}


    order by paytime desc

limit #{start},#{end}

select count(1) count
from (
select paytime
from (
select DATE_FORMAT(create_time,${timeFormat}) paytime
from pay_order
where create_time is not null

    and create_time   >=   #{startTime}


    and create_time   <   #{endTime}

) a
GROUP BY paytime
union
select paytime
from (
select DATE_FORMAT(UPDATE_TIME,${timeFormat}) paytime
from pay_cash
where pay_status=5 and user_type=0

    and pay_time   >=   #{startTime}


    and pay_time   <   #{endTime}

) a
GROUP BY paytime
) b

