mapper.select:
select fd1.id id,fd1.`name` `name`,fd1.fid fid,fd2.`name` fname,fd1.user_id userId,fd1.type `type`
from sys_folder fd1
left join sys_folder fd2 on fd1.fid=fd2.id


        and fd1.`name` like "%"#{name}"%"


        and fd1.`type`=#{type}


limit #{start},#{end}

SELECT count(1) count from sys_folder fd1 left join sys_folder fd2 on fd1.fid=fd2.id
and fd1.`name` like "%"#{name}"%"
and fd1.`type`=#{type}

select fd1.id id,fd1.`name` `name`,fd1.fid fid,fd2.`name` fname,fd1.user_id userId,fd1.type `type` from sys_folder fd1 left join sys_folder fd2 on fd1.fid=fd2.id
and fd1.`name` like "%"#{name}"%"
and fd1.`type`=#{type}

mapper.update:
update sys_folder
set fid=#{fid}

    ,name=#{name}

where id=#{id}
